import { ProductConsumer } from "../Context";
import Instrument from "./Instrument";
import Title from "./Title";
import React, { Component } from "react";
import style from "../css/mainFilters/style.module.css"

export default class InstrumentList extends Component {
  render() {
    return (
      <React.Fragment>
        <div className="py-5">
          <div className="container">
            <Title category="Instruments" />
            <div className={style.filter_wrapper}>
              <p className={style.filter_heading}>Filters:</p>
              <button className={style.filter_button}>Only Guitars</button>
              <button className={style.filter_button}>Only Pianos</button>
              <button className={style.filter_button}>Only Drums</button>
              <button className={style.filter_button}>Only Kalimbas</button>
              <button className={style.filter_button}>Only Bendirs</button>
              <button className={`${style.filter_button} ${style.filter_button_reset}`}>Reset</button>
            </div>
            <div className="row">
              <ProductConsumer>
                {(value) => {
                  return value.storeInstruments
                    .slice(0, 8)
                    .map((instrument) => {
                      return (
                        <Instrument
                          key={instrument.id}
                          instrument={instrument}
                        />
                      );
                    });
                }}
              </ProductConsumer>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}
