import { ProductConsumer } from "../Context";
import Instrument from "./Instrument";
import Title from "./Title";
import style from "../css/mainFilters/style.module.css"
import React, {useState} from 'react';

const InstrumentList = () => {

  let [guitarActive, setGuitarActive] = useState(false)
  let [pianoActive, setPianoActive] = useState(false)
  let [drumActive, setDrumActive] = useState(false)
  let [kalimbaActive, setKalimbaActive] = useState(false)
  let [bendirActive, setBendirActive] = useState(false)



  return (
      <React.Fragment>
        <div className="py-5">
          <div className="container">
            <Title category="Instruments" />
            <div className={style.filter_wrapper}>
              <p className={style.filter_heading}>Filters:</p>
              <button onClick={() => {setGuitarActive(true)}} className={`${guitarActive && style.active} ${style.filter_button}`}>Only Guitars</button>
              <button onClick={() => {setGuitarActive(true)}} className={`${pianoActive && style.active} ${style.filter_button}`}>Only Pianos</button>
              <button onClick={() => {setGuitarActive(true)}} className={`${drumActive && style.active} ${style.filter_button}`}>Only Drums</button>
              <button onClick={() => {setGuitarActive(true)}} className={`${kalimbaActive && style.active} ${style.filter_button}`}>Only Kalimbas</button>
              <button onClick={() => {setGuitarActive(true)}} className={`${bendirActive && style.active} ${style.filter_button}`}>Only Bendirs</button>
              <button onClick={() => {setGuitarActive(true)}} className={`${style.filter_button} ${style.filter_button_reset}`}>Reset</button>
            </div>
            <div className="row">
              <ProductConsumer>
                {(value) => {
                  return value.storeInstruments
                      .slice(0, 8)
                      .map((instrument) => {
                        return (
                            <Instrument
                                key={instrument.id}
                                instrument={instrument}
                            />
                        );
                      });
                }}
              </ProductConsumer>
            </div>
          </div>
        </div>
      </React.Fragment>
  );
};

export default InstrumentList;
